import React, { useState } from "react";
import "../assets/appointment.css";

const DoctorModal = ({ doctor, onClose }) => {
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    reason: "",
    dateTime: "",
    email: "",
  });

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    sendEmail();
  };

  const sendEmail = () => {
    const emailContent = `
            Doctor: ${doctor.name} (${doctor.specialization})
            Name: ${formData.name}
            Age: ${formData.age}
            Reason: ${formData.reason}
            Appointment Time: ${formData.dateTime}
            Email: ${formData.email}
        `;

    // Here, you would typically use a backend service like Node.js/Express with Nodemailer
    // or a third-party service like SendGrid, Mailgun, or EmailJS to send the email.

    console.log("Sending email with appointment details:", emailContent);
    // Close the modal after sending the email
    onClose();
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>{doctor.name}</h2>
        <p>Specialization: {doctor.specialization}</p>

        <form onSubmit={handleSubmit}>
          <div>
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div>
            <label>Age:</label>
            <input
              type="number"
              name="age"
              value={formData.age}
              onChange={handleInputChange}
              required
            />
          </div>
          <div>
            <label>Reason for Appointment:</label>
            <input
              type="text"
              name="reason"
              value={formData.reason}
              onChange={handleInputChange}
              required
            />
          </div>
          <div>
            <label>Appointment Date and Time:</label>
            <input
              type="datetime-local"
              name="dateTime"
              value={formData.dateTime}
              onChange={handleInputChange}
              required
            />
          </div>
          <div>
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit">Book Appointment</button>
        </form>

        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default DoctorModal;
