import React, { useState } from "react";
import "../assets/ChatBot.css"; // Assuming the updated CSS file from before

export default function Chatbot() {
  const [isChatOpen, setIsChatOpen] = useState(false);

  // Function to toggle chat window visibility
  const toggleChat = () => {
    setIsChatOpen(!isChatOpen);
  };

  return (
    <div id="chat-widget">
      <button id="chat-button" onClick={toggleChat}>
        Chat with us!
      </button>

      {/* Chat box visibility based on state */}
      {isChatOpen && (
        <div id="chat-box">
          <div id="chat-box-header">
            <span>Chat with us!</span>
            <button onClick={toggleChat}>Close</button>
          </div>
          <div id="chat-box-content">
            {/* You can add chat history here if needed */}
            <p>How can we help you today?</p>
          </div>
          <div id="chat-box-input">
            <input
              type="text"
              id="user-input"
              placeholder="Type your message..."
            />
            <button id="send-button" onClick={() => alert("Message Sent")}>
              Send
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
